const types = ['❤️', '♠️', '♦️', '♣️'];
const values = [
  { number: 1, name: 'A' },
  { number: 2, name: '2' },
  { number: 3, name: '3' },
  { number: 4, name: '4' },
  { number: 5, name: '5' },
  { number: 6, name: '6' },
  { number: 7, name: '7' },
  { number: 8, name: '8' },
  { number: 9, name: '9' },
  { number: 10, name: '10' },
  { number: 10, name: 'J' },
  { number: 10, name: 'Q' },
  { number: 10, name: 'K' },
];

const randomizedDeck = () => {
  let mySet = new Set();
  do {
    let card = Object.assign({}, values[Math.floor(Math.random() * values.length)]);
    card.type = types[Math.floor(Math.random() * types.length)];
    mySet.add(card);
  } while (mySet.size < 52);
  if (mySet.size === 52) return mySet;
};

exports.randomizedDeck = randomizedDeck;
